app.script:
( function () {

  function org_meadhbh_clock( id ) {
    this.id = id;
    this.el = document.getElementById(id).children[1];
    this.container = this.el.children[0];
    this.clock = this.container.children[0];
    this.date = this.container.children[1];
  }

  org_meadhbh_clock.prototype.init = function ( ) {
    var that = this;
    this.time_interval = setInterval( function () {
      that.clock.innerHTML = (new Date()).toTimeString().substr(0,8);
      that.date.innerHTML = (new Date()).toDateString();
    }, 1000 );
  };

  window.org_meadhbh_clock = org_meadhbh_clock;
} ) ();
